Accept: application/json


const headers = {
  'Accept':'application/json',
  'api-key':'YOUR_API_KEY'
};

fetch('https://api.staging.goshare.co/v1/api-key/verify',
{
  method: 'GET',

  headers: headers
})
.then(function(res) {
    return res.json();
}).then(function(body) {
    console.log(body);
});

